pragma solidity ^0.4.17;

contract PetCard {
    struct Card {
        string code;
        uint256 value;
        address owner;
    }
    Card[] cards;
    mapping (address => uint[]) userCardDict;
    address CEO;
    function PetCard() public payable {
        CEO = msg.sender;
    }
    function buyCard(uint cardId) public returns (bool) {
        address buyer = msg.sender;
        if (cardId >= cards.length || cardId < 0) {
            return false;       
        }
        Card card = cards[cardId];
        if (msg.value < card.value) {
            return false;
        } 
        address myAddress = this;
        if (card.owner != myAddress) {
            return false;
        }
        card.owner = buyer;
        userCardDict[buyer].push(cardId);
        return true;
    }
}
